import{_ as o,o as e,c as i,ag as a}from"./chunks/framework.BDo2gMlJ.js";const u=JSON.parse('{"title":"Install Whiteboard Desktop","description":"","frontmatter":{},"headers":[],"relativePath":"desktop/installation.md","filePath":"desktop/installation.md"}'),n={name:"desktop/installation.md"};function r(l,t,s,d,h,c){return e(),i("div",null,[...t[0]||(t[0]=[a('<h1 id="install-whiteboard-desktop" tabindex="-1">Install Whiteboard Desktop <a class="header-anchor" href="#install-whiteboard-desktop" aria-label="Permalink to &quot;Install Whiteboard Desktop&quot;">​</a></h1><p>The desktop app is built with <strong>Tauri</strong>. It&#39;s lightweight, fast, and optimized for screen recording.</p><h2 id="_1-download" tabindex="-1">1. Download <a class="header-anchor" href="#_1-download" aria-label="Permalink to &quot;1. Download&quot;">​</a></h2><p>Visit the <a href="https://github.com/TheGhoul21/whiteboard/releases" target="_blank" rel="noreferrer">GitHub Releases page</a> and grab the installer for your system:</p><ul><li><strong>macOS:</strong> <code>whiteboard-macos-universal.dmg</code> (Works on both Intel and M1/M2/M3)</li><li><strong>Windows:</strong> <code>whiteboard-windows-x64.msi</code></li><li><strong>Linux:</strong> <code>whiteboard-linux-x64.AppImage</code></li></ul><hr><h2 id="_2-quick-install" tabindex="-1">2. Quick Install <a class="header-anchor" href="#_2-quick-install" aria-label="Permalink to &quot;2. Quick Install&quot;">​</a></h2><p>::: tabs</p><p>== macOS</p><ol><li>Open the <code>.dmg</code> file.</li><li>Drag <strong>Whiteboard</strong> to your <strong>Applications</strong> folder.</li><li><strong>Crucial:</strong> Right-click the app and choose <strong>Open</strong> the first time (to bypass Apple&#39;s unsigned app warning).</li></ol><p>== Windows</p><ol><li>Run the <code>.msi</code> installer.</li><li>Follow the 3-step wizard.</li><li>Launch from the Start Menu.</li></ol><p>== Linux</p><ol><li>Right-click the <code>.AppImage</code> → <strong>Properties</strong> → <strong>Permissions</strong>.</li><li>Check <strong>&quot;Allow executing file as program&quot;</strong>.</li><li>Double-click to run.</li></ol><p>:::</p><hr><h2 id="_3-first-launch-checklist" tabindex="-1">3. First Launch Checklist <a class="header-anchor" href="#_3-first-launch-checklist" aria-label="Permalink to &quot;3. First Launch Checklist&quot;">​</a></h2><p>Once you open the app, ensure everything is ready for recording:</p><ol><li><strong>Open Presentation Window:</strong> Click the button in the bottom-right.</li><li><strong>Test Sync:</strong> Draw something in the main window; it should appear instantly in the second one.</li><li><strong>OBS Setup:</strong><ul><li>Add a <strong>Window Capture</strong> source in OBS.</li><li>Select the <strong>&quot;Whiteboard - Presentation&quot;</strong> window.</li><li>You now have a clean recording area without any toolbars!</li></ul></li></ol><hr><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><ul><li><strong>&quot;App is damaged&quot; (macOS):</strong> Run <code>xattr -cr /Applications/Whiteboard.app</code> in your terminal.</li><li><strong>Windows Defender:</strong> Click &quot;More info&quot; → &quot;Run anyway&quot;.</li><li><strong>Linux missing libraries:</strong> Install <code>libwebkit2gtk-4.1-0</code> and <code>libayatana-appindicator3-1</code>.</li></ul><p><a href="/whiteboard/desktop/obs-integration.html">Set up OBS Integration →</a></p>',23)])])}const g=o(n,[["render",r]]);export{u as __pageData,g as default};
