import{_ as o,o as a,c as i,ag as r}from"./chunks/framework.Bo334zLR.js";const p=JSON.parse('{"title":"Basic Drawing","description":"","frontmatter":{},"headers":[],"relativePath":"guide/basic-drawing.md","filePath":"guide/basic-drawing.md"}'),t={name:"guide/basic-drawing.md"};function l(s,e,n,c,h,d){return a(),i("div",null,[...e[0]||(e[0]=[r('<h1 id="basic-drawing" tabindex="-1">Basic Drawing <a class="header-anchor" href="#basic-drawing" aria-label="Permalink to &quot;Basic Drawing&quot;">​</a></h1><p>Learn the fundamental drawing tools and techniques in Whiteboard.</p><h2 id="drawing-tools" tabindex="-1">Drawing Tools <a class="header-anchor" href="#drawing-tools" aria-label="Permalink to &quot;Drawing Tools&quot;">​</a></h2><h3 id="pen-tool" tabindex="-1">Pen Tool <a class="header-anchor" href="#pen-tool" aria-label="Permalink to &quot;Pen Tool&quot;">​</a></h3><p>The standard drawing tool for creating precise strokes.</p><p><strong>Activate:</strong> Click pen icon or press <code>P</code></p><p><strong>Properties:</strong></p><ul><li>Solid color strokes</li><li>Configurable size (1-50px)</li><li>Pressure sensitivity (if using tablet)</li><li>Perfect for writing and line work</li></ul><p><strong>Use cases:</strong></p><ul><li>Writing text by hand</li><li>Drawing diagrams</li><li>Creating precise lines</li><li>Technical illustrations</li></ul><h3 id="smooth-pen-tool" tabindex="-1">Smooth Pen Tool <a class="header-anchor" href="#smooth-pen-tool" aria-label="Permalink to &quot;Smooth Pen Tool&quot;">​</a></h3><p>Advanced pen with calligraphy-style strokes.</p><p><strong>Activate:</strong> Click smooth pen icon or press <code>S</code></p><p><strong>Properties:</strong></p><ul><li>Variable width based on speed</li><li>Smooth, flowing strokes</li><li>Pressure-sensitive tapering</li><li>Uses perfect-freehand algorithm</li></ul><p><strong>Use cases:</strong></p><ul><li>Natural handwriting</li><li>Artistic drawings</li><li>Emphasis strokes</li><li>Smooth curves</li></ul><p><strong>How it works:</strong></p><ul><li>Draw fast → thinner stroke</li><li>Draw slow → thicker stroke</li><li>Natural pen feel</li></ul><h3 id="highlighter-tool" tabindex="-1">Highlighter Tool <a class="header-anchor" href="#highlighter-tool" aria-label="Permalink to &quot;Highlighter Tool&quot;">​</a></h3><p>Semi-transparent highlighting tool.</p><p><strong>Activate:</strong> Click highlighter icon or press <code>L</code></p><p><strong>Properties:</strong></p><ul><li>40% opacity</li><li>Wide stroke (default)</li><li>Bright colors recommended</li><li>Overlays existing content</li></ul><p><strong>Use cases:</strong></p><ul><li>Highlighting important text</li><li>Marking sections</li><li>Creating emphasis</li><li>Color coding</li></ul><p><strong>Tips:</strong></p><ul><li>Use bright colors (yellow, orange, pink)</li><li>Overlap for darker highlighting</li><li>Great for emphasizing diagrams</li></ul><h3 id="eraser-tool" tabindex="-1">Eraser Tool <a class="header-anchor" href="#eraser-tool" aria-label="Permalink to &quot;Eraser Tool&quot;">​</a></h3><p>Remove individual strokes from the canvas.</p><p><strong>Activate:</strong> Click eraser icon or press <code>E</code></p><p><strong>How it works:</strong></p><ul><li>Click on stroke → removes entire stroke</li><li>Hover to preview which stroke will be erased</li><li>No partial erasing (removes complete strokes)</li></ul><p><strong>Tips:</strong></p><ul><li>Undo (Cmd/Ctrl+Z) for more precise removal</li><li>Use frames to organize instead of erasing</li><li>Can&#39;t erase smart objects (use delete instead)</li></ul><h2 id="canvas-navigation" tabindex="-1">Canvas Navigation <a class="header-anchor" href="#canvas-navigation" aria-label="Permalink to &quot;Canvas Navigation&quot;">​</a></h2><h3 id="pan-move-canvas" tabindex="-1">Pan (Move Canvas) <a class="header-anchor" href="#pan-move-canvas" aria-label="Permalink to &quot;Pan (Move Canvas)&quot;">​</a></h3><p><strong>Hand Tool:</strong> Press <code>H</code> or hold <code>Space</code></p><p><strong>Usage:</strong></p><ul><li>Click and drag to move canvas</li><li>Navigate large diagrams</li><li>Position content in view</li></ul><p><strong>Multi-finger:</strong> Two-finger drag on trackpad</p><h3 id="zoom" tabindex="-1">Zoom <a class="header-anchor" href="#zoom" aria-label="Permalink to &quot;Zoom&quot;">​</a></h3><p><strong>Shortcuts:</strong></p><ul><li><code>Cmd/Ctrl + Scroll</code>: Zoom in/out</li><li><code>Cmd/Ctrl + +</code>: Zoom in</li><li><code>Cmd/Ctrl + -</code>: Zoom out</li><li><code>Cmd/Ctrl + 0</code>: Reset zoom to 100%</li></ul><p><strong>Pinch:</strong> Pinch gesture on trackpad</p><p><strong>Current zoom level:</strong> Shown in bottom-right corner</p><h2 id="color-selection" tabindex="-1">Color Selection <a class="header-anchor" href="#color-selection" aria-label="Permalink to &quot;Color Selection&quot;">​</a></h2><h3 id="color-picker" tabindex="-1">Color Picker <a class="header-anchor" href="#color-picker" aria-label="Permalink to &quot;Color Picker&quot;">​</a></h3><p>Click current color to open picker:</p><p><strong>Recent colors:</strong> Quick access to recently used colors</p><p><strong>Preset palette:</strong> Common colors for quick selection</p><p><strong>Custom colors:</strong></p><ul><li>Click custom area</li><li>Drag to select hue and saturation</li><li>Or enter hex code (e.g., <code>#FF5733</code>)</li></ul><h3 id="color-shortcuts" tabindex="-1">Color Shortcuts <a class="header-anchor" href="#color-shortcuts" aria-label="Permalink to &quot;Color Shortcuts&quot;">​</a></h3><p>Quick color access:</p><ul><li>Recent colors shown in toolbar</li><li>Click to switch instantly</li><li>No need to open picker</li></ul><h2 id="stroke-size" tabindex="-1">Stroke Size <a class="header-anchor" href="#stroke-size" aria-label="Permalink to &quot;Stroke Size&quot;">​</a></h2><p>Adjust stroke thickness for current tool:</p><p><strong>Size slider:</strong> Drag to adjust (1-50px)</p><p><strong>Preview:</strong> Live preview shows exact size</p><p><strong>Per-tool:</strong> Each tool remembers its size</p><ul><li>Pen: 2px default</li><li>Smooth pen: 4px default</li><li>Highlighter: 20px default</li></ul><h2 id="drawing-techniques" tabindex="-1">Drawing Techniques <a class="header-anchor" href="#drawing-techniques" aria-label="Permalink to &quot;Drawing Techniques&quot;">​</a></h2><h3 id="straight-lines" tabindex="-1">Straight Lines <a class="header-anchor" href="#straight-lines" aria-label="Permalink to &quot;Straight Lines&quot;">​</a></h3><p><strong>Without tool:</strong></p><ol><li>Click starting point</li><li>Hold Shift</li><li>Click ending point</li><li>Straight line drawn</li></ol><p><strong>With pen:</strong> Draw quickly in one motion for straighter lines</p><h3 id="shapes" tabindex="-1">Shapes <a class="header-anchor" href="#shapes" aria-label="Permalink to &quot;Shapes&quot;">​</a></h3><p>Use shape tools for perfect geometry:</p><p><strong>Rectangle:</strong></p><ol><li>Select rectangle tool</li><li>Click and drag</li><li>Release for completed rectangle</li></ol><p><strong>Circle:</strong></p><ol><li>Select circle tool</li><li>Click and drag</li><li>Release for completed circle</li></ol><p><strong>Arrow:</strong></p><ol><li>Select arrow tool</li><li>Click and drag for direction</li><li>Arrow head automatically added</li></ol><p><a href="/whiteboard/docs/features/drawing-tools.html">Learn more about shapes →</a></p><h3 id="free-drawing" tabindex="-1">Free Drawing <a class="header-anchor" href="#free-drawing" aria-label="Permalink to &quot;Free Drawing&quot;">​</a></h3><p><strong>Tips for smooth drawing:</strong></p><ol><li>Draw at consistent speed</li><li>Use smooth pen for natural feel</li><li>Zoom in for detailed work</li><li>Use stylus/tablet for best control</li></ol><h2 id="backgrounds" tabindex="-1">Backgrounds <a class="header-anchor" href="#backgrounds" aria-label="Permalink to &quot;Backgrounds&quot;">​</a></h2><p>Change canvas background:</p><p><strong>Options:</strong></p><ul><li><strong>None</strong>: White/transparent</li><li><strong>Grid</strong>: Dot grid pattern</li><li><strong>Lines</strong>: Ruled lines (notebook style)</li><li><strong>Dark</strong>: Dark mode background</li></ul><p><strong>Change background:</strong> Click background icon in toolbar</p><p><strong>Per-frame:</strong> Each frame can have different background</p><h2 id="undo-redo" tabindex="-1">Undo &amp; Redo <a class="header-anchor" href="#undo-redo" aria-label="Permalink to &quot;Undo &amp; Redo&quot;">​</a></h2><p><strong>Undo:</strong> <code>Cmd/Ctrl + Z</code><strong>Redo:</strong> <code>Cmd/Ctrl + Shift + Z</code></p><p><strong>What can be undone:</strong></p><ul><li>Drawing strokes</li><li>Adding shapes</li><li>Adding text/objects</li><li>Deletions</li></ul><p><strong>History depth:</strong> Last 100 actions</p><h2 id="selection" tabindex="-1">Selection <a class="header-anchor" href="#selection" aria-label="Permalink to &quot;Selection&quot;">​</a></h2><p>Currently, there&#39;s no select tool. To modify content:</p><p><strong>Move objects:</strong> Click and drag smart objects (text, code, images)</p><p><strong>Modify strokes:</strong> Use magic wand to perfect shapes</p><p><strong>Remove content:</strong> Use eraser or undo</p><h2 id="layers" tabindex="-1">Layers <a class="header-anchor" href="#layers" aria-label="Permalink to &quot;Layers&quot;">​</a></h2><p>Canvas uses implicit layering:</p><p><strong>Order:</strong></p><ul><li>Newer strokes appear above older ones</li><li>Smart objects layer based on creation order</li><li>No manual layer reordering (yet)</li></ul><p><strong>Spotlight always on top:</strong> Spotlight appears above all content</p><h2 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h2><h3 id="for-clean-diagrams" tabindex="-1">For Clean Diagrams <a class="header-anchor" href="#for-clean-diagrams" aria-label="Permalink to &quot;For Clean Diagrams&quot;">​</a></h3><ol><li>✅ Use shapes for geometric forms</li><li>✅ Use pen for connecting lines</li><li>✅ Use text objects for labels</li><li>✅ Plan layout before drawing</li><li>✅ Use frames for organization</li></ol><h3 id="for-natural-handwriting" tabindex="-1">For Natural Handwriting <a class="header-anchor" href="#for-natural-handwriting" aria-label="Permalink to &quot;For Natural Handwriting&quot;">​</a></h3><ol><li>✅ Use smooth pen tool</li><li>✅ Adjust size to preference (3-5px)</li><li>✅ Write at natural speed</li><li>✅ Use pressure-sensitive tablet</li><li>✅ Zoom in for small text</li></ol><h3 id="for-highlighting" tabindex="-1">For Highlighting <a class="header-anchor" href="#for-highlighting" aria-label="Permalink to &quot;For Highlighting&quot;">​</a></h3><ol><li>✅ Draw content first</li><li>✅ Add highlights after</li><li>✅ Use bright, contrasting colors</li><li>✅ Don&#39;t over-highlight</li><li>✅ Consider laser pointer for temporary emphasis</li></ol><h2 id="common-issues" tabindex="-1">Common Issues <a class="header-anchor" href="#common-issues" aria-label="Permalink to &quot;Common Issues&quot;">​</a></h2><h3 id="strokes-look-jagged" tabindex="-1">Strokes look jagged <a class="header-anchor" href="#strokes-look-jagged" aria-label="Permalink to &quot;Strokes look jagged&quot;">​</a></h3><p><strong>Solutions:</strong></p><ul><li>Use smooth pen tool instead</li><li>Zoom in while drawing</li><li>Draw more slowly</li><li>Check if using mouse vs. tablet</li></ul><h3 id="can-t-draw-on-canvas" tabindex="-1">Can&#39;t draw on canvas <a class="header-anchor" href="#can-t-draw-on-canvas" aria-label="Permalink to &quot;Can&#39;t draw on canvas&quot;">​</a></h3><p><strong>Check:</strong></p><ul><li>Not using hand tool (switch to pen)</li><li>Canvas is focused (click on canvas first)</li><li>Not in presentation window (use control window)</li></ul><h3 id="colors-not-changing" tabindex="-1">Colors not changing <a class="header-anchor" href="#colors-not-changing" aria-label="Permalink to &quot;Colors not changing&quot;">​</a></h3><p><strong>Check:</strong></p><ul><li>Color picker is actually selecting different color</li><li>Current tool supports color (eraser doesn&#39;t)</li><li>Refresh if colors seem stuck</li></ul><h3 id="drawing-feels-laggy" tabindex="-1">Drawing feels laggy <a class="header-anchor" href="#drawing-feels-laggy" aria-label="Permalink to &quot;Drawing feels laggy&quot;">​</a></h3><p><strong>Solutions:</strong></p><ul><li>Reduce zoom level</li><li>Clear canvas or use new frame</li><li>Close other applications</li><li>Check CPU usage</li></ul><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ul><li><a href="/whiteboard/docs/guide/smart-objects.html">Learn about Smart Objects →</a></li><li><a href="/whiteboard/docs/guide/shortcuts.html">Master Keyboard Shortcuts →</a></li><li><a href="/whiteboard/docs/features/">Explore Advanced Features →</a></li></ul>',122)])])}const u=o(t,[["render",l]]);export{p as __pageData,u as default};
