import{_ as e,o as a,c as o,ag as r}from"./chunks/framework.BDo2gMlJ.js";const u=JSON.parse('{"title":"Getting Started","description":"","frontmatter":{},"headers":[],"relativePath":"guide/getting-started.md","filePath":"guide/getting-started.md"}'),i={name:"guide/getting-started.md"};function s(l,t,n,h,d,p){return a(),o("div",null,[...t[0]||(t[0]=[r('<h1 id="getting-started" tabindex="-1">Getting Started <a class="header-anchor" href="#getting-started" aria-label="Permalink to &quot;Getting Started&quot;">​</a></h1><p>Welcome to Whiteboard! This guide will help you get up and running in minutes.</p><h2 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation&quot;">​</a></h2><p>Download and install the Whiteboard desktop app for advanced features like dual-window presentation mode and OBS integration.</p><p><strong>Features:</strong></p><ul><li>Dual-window presentation mode</li><li>Laser pointer &amp; spotlight tools</li><li>OBS integration</li><li>Better performance</li><li>Offline access</li><li>Cross-platform (macOS, Windows, Linux)</li></ul><p><a href="/whiteboard/desktop/installation.html">Install Desktop App →</a></p><h2 id="first-steps" tabindex="-1">First Steps <a class="header-anchor" href="#first-steps" aria-label="Permalink to &quot;First Steps&quot;">​</a></h2><h3 id="_1-launch-the-application" tabindex="-1">1. Launch the Application <a class="header-anchor" href="#_1-launch-the-application" aria-label="Permalink to &quot;1. Launch the Application&quot;">​</a></h3><p><strong>Web Version:</strong> Simply open the URL in your browser.</p><p><strong>Desktop App:</strong> Open the installed application from your Applications folder (macOS), Start Menu (Windows), or application launcher (Linux).</p><h3 id="_2-basic-controls" tabindex="-1">2. Basic Controls <a class="header-anchor" href="#_2-basic-controls" aria-label="Permalink to &quot;2. Basic Controls&quot;">​</a></h3><p>The toolbar at the top contains all your drawing tools:</p><ul><li><strong>Hand Tool</strong> (H): Pan the canvas</li><li><strong>Pen</strong> (P): Basic drawing</li><li><strong>Smooth Pen</strong> (S): Calligraphy-style drawing</li><li><strong>Highlighter</strong> (L): Semi-transparent highlighting</li><li><strong>Eraser</strong> (E): Remove strokes</li><li><strong>Shapes</strong>: Rectangle, Circle, Arrow</li><li><strong>Text</strong> (T): Add text objects</li><li><strong>Laser Pointer</strong>: Temporary pointer that fades</li><li><strong>Spotlight</strong> (Shift+P): Highlight areas with spotlight effect</li></ul><h3 id="_3-your-first-drawing" tabindex="-1">3. Your First Drawing <a class="header-anchor" href="#_3-your-first-drawing" aria-label="Permalink to &quot;3. Your First Drawing&quot;">​</a></h3><ol><li>Select the <strong>Pen tool</strong> (or press <code>P</code>)</li><li>Choose a color from the color picker</li><li>Draw on the canvas with your mouse or stylus</li><li>Use the <strong>Hand tool</strong> (<code>H</code>) to pan around</li><li>Scroll to zoom in/out</li></ol><h3 id="_4-save-your-work" tabindex="-1">4. Save Your Work <a class="header-anchor" href="#_4-save-your-work" aria-label="Permalink to &quot;4. Save Your Work&quot;">​</a></h3><p>Whiteboard automatically saves your canvas to browser localStorage every 10 seconds. Your work persists across sessions.</p><p>To export:</p><ul><li><strong>JSON</strong>: Save/load complete canvas state</li><li><strong>PNG</strong>: Export current view as image (coming soon)</li><li><strong>PDF</strong>: Export to PDF document (coming soon)</li></ul><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ul><li><a href="/whiteboard/guide/basic-drawing.html">Learn Basic Drawing →</a></li><li><a href="/whiteboard/guide/smart-objects.html">Explore Smart Objects →</a></li><li><a href="/whiteboard/guide/shortcuts.html">Master Keyboard Shortcuts →</a></li><li><a href="/whiteboard/desktop/presentation-mode.html">Set Up Presentation Mode →</a> (Desktop only)</li></ul><h2 id="need-help" tabindex="-1">Need Help? <a class="header-anchor" href="#need-help" aria-label="Permalink to &quot;Need Help?&quot;">​</a></h2><ul><li>Check the <a href="/whiteboard/features/">Features documentation</a></li><li>Join our community discussions on GitHub</li><li>Report bugs or request features via <a href="https://github.com/TheGhoul21/whiteboard/issues" target="_blank" rel="noreferrer">GitHub Issues</a></li></ul>',24)])])}const g=e(i,[["render",s]]);export{u as __pageData,g as default};
