import{_ as e,o,c as a,ag as i}from"./chunks/framework.Bo334zLR.js";const p=JSON.parse('{"title":"Keyboard Shortcuts","description":"","frontmatter":{},"headers":[],"relativePath":"guide/shortcuts.md","filePath":"guide/shortcuts.md"}'),r={name:"guide/shortcuts.md"};function d(l,t,n,s,c,h){return o(),a("div",null,[...t[0]||(t[0]=[i(`<h1 id="keyboard-shortcuts" tabindex="-1">Keyboard Shortcuts <a class="header-anchor" href="#keyboard-shortcuts" aria-label="Permalink to &quot;Keyboard Shortcuts&quot;">​</a></h1><p>Master Whiteboard with these keyboard shortcuts for efficient content creation.</p><h2 id="tool-selection" tabindex="-1">Tool Selection <a class="header-anchor" href="#tool-selection" aria-label="Permalink to &quot;Tool Selection&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Shortcut</th><th>Tool</th></tr></thead><tbody><tr><td><code>H</code></td><td>Hand (Pan)</td></tr><tr><td><code>P</code></td><td>Pen</td></tr><tr><td><code>S</code></td><td>Smooth Pen</td></tr><tr><td><code>L</code></td><td>Highlighter</td></tr><tr><td><code>E</code></td><td>Eraser</td></tr><tr><td><code>T</code></td><td>Text</td></tr><tr><td><code>M</code></td><td>Magic Wand</td></tr><tr><td><code>Shift+P</code></td><td>Spotlight/Pointer</td></tr></tbody></table><p><strong>Tip:</strong> Press the key while drawing to quickly switch tools without interrupting workflow.</p><h2 id="canvas-navigation" tabindex="-1">Canvas Navigation <a class="header-anchor" href="#canvas-navigation" aria-label="Permalink to &quot;Canvas Navigation&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Shortcut</th><th>Action</th></tr></thead><tbody><tr><td><code>Space + Drag</code></td><td>Pan canvas</td></tr><tr><td><code>Cmd/Ctrl + Scroll</code></td><td>Zoom in/out</td></tr><tr><td><code>Cmd/Ctrl + +</code></td><td>Zoom in</td></tr><tr><td><code>Cmd/Ctrl + -</code></td><td>Zoom out</td></tr><tr><td><code>Cmd/Ctrl + 0</code></td><td>Reset zoom to 100%</td></tr></tbody></table><p><strong>Trackpad:</strong></p><ul><li>Two-finger drag: Pan</li><li>Pinch: Zoom</li></ul><h2 id="edit-operations" tabindex="-1">Edit Operations <a class="header-anchor" href="#edit-operations" aria-label="Permalink to &quot;Edit Operations&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Shortcut</th><th>Action</th></tr></thead><tbody><tr><td><code>Cmd/Ctrl + Z</code></td><td>Undo</td></tr><tr><td><code>Cmd/Ctrl + Shift + Z</code></td><td>Redo</td></tr><tr><td><code>Cmd/Ctrl + C</code></td><td>Copy (coming soon)</td></tr><tr><td><code>Cmd/Ctrl + V</code></td><td>Paste image/code/LaTeX</td></tr><tr><td><code>Cmd/Ctrl + X</code></td><td>Cut (coming soon)</td></tr><tr><td><code>Delete</code></td><td>Delete selected object</td></tr><tr><td><code>Backspace</code></td><td>Delete selected object</td></tr></tbody></table><h2 id="object-manipulation" tabindex="-1">Object Manipulation <a class="header-anchor" href="#object-manipulation" aria-label="Permalink to &quot;Object Manipulation&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Shortcut</th><th>Action</th></tr></thead><tbody><tr><td><code>Arrow Keys</code></td><td>Nudge object 1px</td></tr><tr><td><code>Shift + Arrow</code></td><td>Nudge object 10px</td></tr><tr><td><code>Double-click</code></td><td>Edit object (text, code, etc.)</td></tr><tr><td><code>Shift + Drag</code></td><td>Maintain aspect ratio (resize)</td></tr></tbody></table><h2 id="frame-management" tabindex="-1">Frame Management <a class="header-anchor" href="#frame-management" aria-label="Permalink to &quot;Frame Management&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Shortcut</th><th>Action</th></tr></thead><tbody><tr><td><code>Cmd/Ctrl + N</code></td><td>New frame (coming soon)</td></tr><tr><td><code>Cmd/Ctrl + D</code></td><td>Duplicate frame (coming soon)</td></tr><tr><td><code>Cmd/Ctrl + [</code></td><td>Previous frame</td></tr><tr><td><code>Cmd/Ctrl + ]</code></td><td>Next frame</td></tr></tbody></table><h2 id="presentation-mode-desktop-app" tabindex="-1">Presentation Mode (Desktop App) <a class="header-anchor" href="#presentation-mode-desktop-app" aria-label="Permalink to &quot;Presentation Mode (Desktop App)&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Shortcut</th><th>Action</th></tr></thead><tbody><tr><td><code>Cmd/Ctrl + Shift + P</code></td><td>Toggle presentation window</td></tr><tr><td><code>F11</code></td><td>Toggle fullscreen (in presentation window)</td></tr><tr><td><code>Esc</code></td><td>Exit fullscreen</td></tr></tbody></table><h2 id="drawing-modifiers" tabindex="-1">Drawing Modifiers <a class="header-anchor" href="#drawing-modifiers" aria-label="Permalink to &quot;Drawing Modifiers&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Shortcut</th><th>Effect</th></tr></thead><tbody><tr><td><code>Shift + Click</code></td><td>Draw straight line to clicked point</td></tr><tr><td><code>Shift + Drag</code></td><td>Constrain shapes to square/circle</td></tr></tbody></table><h2 id="file-operations" tabindex="-1">File Operations <a class="header-anchor" href="#file-operations" aria-label="Permalink to &quot;File Operations&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Shortcut</th><th>Action</th></tr></thead><tbody><tr><td><code>Cmd/Ctrl + S</code></td><td>Save (auto-save already active)</td></tr><tr><td><code>Cmd/Ctrl + O</code></td><td>Open (coming soon)</td></tr><tr><td><code>Cmd/Ctrl + E</code></td><td>Export (coming soon)</td></tr></tbody></table><h2 id="view-options" tabindex="-1">View Options <a class="header-anchor" href="#view-options" aria-label="Permalink to &quot;View Options&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Shortcut</th><th>Action</th></tr></thead><tbody><tr><td><code>Cmd/Ctrl + B</code></td><td>Toggle background type</td></tr><tr><td><code>Cmd/Ctrl + G</code></td><td>Toggle grid</td></tr><tr><td><code>F</code></td><td>Fit to screen (coming soon)</td></tr></tbody></table><h2 id="quick-actions" tabindex="-1">Quick Actions <a class="header-anchor" href="#quick-actions" aria-label="Permalink to &quot;Quick Actions&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Shortcut</th><th>Action</th></tr></thead><tbody><tr><td><code>?</code></td><td>Show help (coming soon)</td></tr><tr><td><code>Cmd/Ctrl + K</code></td><td>Command palette (coming soon)</td></tr></tbody></table><h2 id="platform-specific" tabindex="-1">Platform-Specific <a class="header-anchor" href="#platform-specific" aria-label="Permalink to &quot;Platform-Specific&quot;">​</a></h2><h3 id="macos" tabindex="-1">macOS <a class="header-anchor" href="#macos" aria-label="Permalink to &quot;macOS&quot;">​</a></h3><ul><li><code>Cmd</code> = Command key (⌘)</li><li><code>Option</code> = Alt key (⌥)</li><li><code>Control</code> = Control key (⌃)</li></ul><h3 id="windows-linux" tabindex="-1">Windows/Linux <a class="header-anchor" href="#windows-linux" aria-label="Permalink to &quot;Windows/Linux&quot;">​</a></h3><ul><li><code>Ctrl</code> = Control key</li><li><code>Alt</code> = Alt key</li></ul><p><strong>Note:</strong> Most shortcuts use <code>Cmd</code> on macOS and <code>Ctrl</code> on Windows/Linux.</p><h2 id="custom-shortcuts" tabindex="-1">Custom Shortcuts <a class="header-anchor" href="#custom-shortcuts" aria-label="Permalink to &quot;Custom Shortcuts&quot;">​</a></h2><p>Currently, shortcuts are fixed. Future release will support customization:</p><ul><li>Rebind any shortcut</li><li>Create custom tool shortcuts</li><li>Export/import shortcut profiles</li></ul><h2 id="tips-tricks" tabindex="-1">Tips &amp; Tricks <a class="header-anchor" href="#tips-tricks" aria-label="Permalink to &quot;Tips &amp; Tricks&quot;">​</a></h2><h3 id="power-user-workflow" tabindex="-1">Power User Workflow <a class="header-anchor" href="#power-user-workflow" aria-label="Permalink to &quot;Power User Workflow&quot;">​</a></h3><p><strong>Left hand on keyboard:</strong></p><ul><li><code>H</code> - Pan with mouse</li><li><code>P</code> - Draw</li><li><code>E</code> - Erase mistakes</li><li><code>T</code> - Add labels</li></ul><p><strong>Right hand on mouse:</strong></p><ul><li>Draw continuously</li><li>Switch tools with left hand</li><li>Never reach for toolbar</li></ul><h3 id="quick-tool-switching" tabindex="-1">Quick Tool Switching <a class="header-anchor" href="#quick-tool-switching" aria-label="Permalink to &quot;Quick Tool Switching&quot;">​</a></h3><p><strong>Without shortcuts:</strong></p><ol><li>Move mouse to toolbar</li><li>Click tool</li><li>Move back to canvas</li><li>Continue drawing</li></ol><p><strong>With shortcuts:</strong></p><ol><li>Press key (e.g., <code>P</code>)</li><li>Immediately continue drawing</li><li>5x faster workflow</li></ol><h3 id="zoom-precision" tabindex="-1">Zoom Precision <a class="header-anchor" href="#zoom-precision" aria-label="Permalink to &quot;Zoom Precision&quot;">​</a></h3><p><strong>Smooth zoom:</strong></p><ul><li>Hold <code>Cmd/Ctrl</code></li><li>Slowly scroll</li><li>Precision zoom to exact level</li></ul><p><strong>Quick zoom:</strong></p><ul><li><code>Cmd/Ctrl + +</code> repeatedly</li><li>Fast zoom in</li><li><code>Cmd/Ctrl + 0</code> to reset</li></ul><h3 id="object-positioning" tabindex="-1">Object Positioning <a class="header-anchor" href="#object-positioning" aria-label="Permalink to &quot;Object Positioning&quot;">​</a></h3><p><strong>Rough positioning:</strong></p><ul><li>Drag object to approximate location</li></ul><p><strong>Fine-tuning:</strong></p><ul><li>Arrow keys for 1px nudges</li><li><code>Shift+Arrow</code> for 10px jumps</li><li>Perfect alignment</li></ul><h2 id="cheat-sheet" tabindex="-1">Cheat Sheet <a class="header-anchor" href="#cheat-sheet" aria-label="Permalink to &quot;Cheat Sheet&quot;">​</a></h2><p>Print-friendly reference:</p><h3 id="essential-shortcuts" tabindex="-1">Essential Shortcuts <a class="header-anchor" href="#essential-shortcuts" aria-label="Permalink to &quot;Essential Shortcuts&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Tools:          Navigation:        Edit:</span></span>
<span class="line"><span>H - Hand        Space+Drag - Pan   Cmd+Z - Undo</span></span>
<span class="line"><span>P - Pen         Cmd+Scroll - Zoom  Cmd+Shift+Z - Redo</span></span>
<span class="line"><span>S - Smooth      Cmd+0 - Reset      Delete - Remove</span></span>
<span class="line"><span>E - Eraser</span></span>
<span class="line"><span>T - Text</span></span>
<span class="line"><span>M - Magic</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Presentation (Desktop):</span></span>
<span class="line"><span>Cmd+Shift+P - Open presentation</span></span>
<span class="line"><span>F11 - Fullscreen</span></span>
<span class="line"><span>Esc - Exit fullscreen</span></span></code></pre></div><h2 id="learning-path" tabindex="-1">Learning Path <a class="header-anchor" href="#learning-path" aria-label="Permalink to &quot;Learning Path&quot;">​</a></h2><h3 id="beginner" tabindex="-1">Beginner <a class="header-anchor" href="#beginner" aria-label="Permalink to &quot;Beginner&quot;">​</a></h3><p>Start with these essential shortcuts:</p><ol><li><code>H</code> - Hand tool for navigation</li><li><code>P</code> - Pen for drawing</li><li><code>Cmd/Ctrl + Z</code> - Undo mistakes</li><li><code>Space + Drag</code> - Pan canvas</li><li><code>Cmd/Ctrl + Scroll</code> - Zoom</li></ol><p><strong>Goal:</strong> Navigate and draw without toolbar clicks.</p><h3 id="intermediate" tabindex="-1">Intermediate <a class="header-anchor" href="#intermediate" aria-label="Permalink to &quot;Intermediate&quot;">​</a></h3><p>Add these for efficiency:</p><ol><li><code>S</code> - Smooth pen for better strokes</li><li><code>E</code> - Eraser for cleanup</li><li><code>T</code> - Text for labels</li><li><code>L</code> - Highlighter for emphasis</li><li><code>Shift + Click</code> - Straight lines</li></ol><p><strong>Goal:</strong> Complete workflow without mouse reaching toolbar.</p><h3 id="advanced" tabindex="-1">Advanced <a class="header-anchor" href="#advanced" aria-label="Permalink to &quot;Advanced&quot;">​</a></h3><p>Master these for professional speed:</p><ol><li><code>M</code> - Magic wand for perfect shapes</li><li><code>Shift + P</code> - Spotlight for presentations</li><li><code>Cmd/Ctrl + Shift + P</code> - Presentation mode</li><li><code>Arrow keys</code> - Precise object positioning</li><li><code>F11</code> - Quick fullscreen toggle</li></ol><p><strong>Goal:</strong> Seamless recording workflow with no UI interaction.</p><h2 id="accessibility" tabindex="-1">Accessibility <a class="header-anchor" href="#accessibility" aria-label="Permalink to &quot;Accessibility&quot;">​</a></h2><h3 id="mouse-free-operation" tabindex="-1">Mouse-Free Operation <a class="header-anchor" href="#mouse-free-operation" aria-label="Permalink to &quot;Mouse-Free Operation&quot;">​</a></h3><p>Currently limited. Future improvements planned:</p><ul><li>Full keyboard navigation</li><li>Accessible tool selection</li><li>Screen reader support</li><li>High contrast mode</li></ul><h3 id="custom-timing" tabindex="-1">Custom Timing <a class="header-anchor" href="#custom-timing" aria-label="Permalink to &quot;Custom Timing&quot;">​</a></h3><p>For users who need more time:</p><ul><li>No time-limited shortcuts</li><li>All shortcuts can be pressed deliberately</li><li>No complex chord requirements (yet)</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="shortcut-not-working" tabindex="-1">Shortcut not working <a class="header-anchor" href="#shortcut-not-working" aria-label="Permalink to &quot;Shortcut not working&quot;">​</a></h3><p><strong>Check:</strong></p><ul><li>Canvas is focused (click canvas first)</li><li>Not typing in text field</li><li>Correct platform modifier (Cmd vs Ctrl)</li><li>No conflicting browser extensions</li></ul><h3 id="wrong-tool-activates" tabindex="-1">Wrong tool activates <a class="header-anchor" href="#wrong-tool-activates" aria-label="Permalink to &quot;Wrong tool activates&quot;">​</a></h3><p><strong>Solutions:</strong></p><ul><li>Ensure using correct key</li><li>Check keyboard layout (QWERTY assumed)</li><li>Disable browser extensions</li><li>Refresh page</li></ul><h3 id="zoom-shortcuts-conflict" tabindex="-1">Zoom shortcuts conflict <a class="header-anchor" href="#zoom-shortcuts-conflict" aria-label="Permalink to &quot;Zoom shortcuts conflict&quot;">​</a></h3><p>If browser zoom interferes:</p><p><strong>Chrome/Edge:</strong></p><ol><li>Settings → Appearance</li><li>Disable browser zoom shortcuts</li><li>Or use trackpad pinch instead</li></ol><p><strong>Firefox:</strong></p><ol><li>about:config</li><li>Modify zoom shortcut preferences</li></ol><h3 id="can-t-remember-shortcuts" tabindex="-1">Can&#39;t remember shortcuts <a class="header-anchor" href="#can-t-remember-shortcuts" aria-label="Permalink to &quot;Can&#39;t remember shortcuts&quot;">​</a></h3><p><strong>Solutions:</strong></p><ul><li>Hover over tools for tooltip hints</li><li>Print cheat sheet above</li><li>Practice one shortcut at a time</li><li>Muscle memory develops quickly</li></ul><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ul><li><a href="/whiteboard/docs/guide/basic-drawing.html">Practice with Basic Drawing →</a></li><li><a href="/whiteboard/docs/guide/smart-objects.html">Try Smart Objects →</a></li><li><a href="/whiteboard/docs/features/">Explore All Features →</a></li></ul><h2 id="shortcut-philosophy" tabindex="-1">Shortcut Philosophy <a class="header-anchor" href="#shortcut-philosophy" aria-label="Permalink to &quot;Shortcut Philosophy&quot;">​</a></h2><p>Whiteboard shortcuts follow these principles:</p><ol><li><strong>Mnemonic:</strong> <code>P</code> for Pen, <code>T</code> for Text, <code>E</code> for Eraser</li><li><strong>Common patterns:</strong> Follow standard editing conventions</li><li><strong>Hand-friendly:</strong> Most tools accessible with left hand</li><li><strong>Non-modal:</strong> Switch tools anytime without mode switching</li><li><strong>Progressive:</strong> Start simple, add advanced shortcuts over time</li></ol><p>Master shortcuts gradually - focus on your most-used tools first!</p>`,101)])])}const m=e(r,[["render",d]]);export{p as __pageData,m as default};
