import{_ as i,o as l,c as a,ag as o}from"./chunks/framework.Bo334zLR.js";const g=JSON.parse('{"title":"Drawing Tools","description":"","frontmatter":{},"headers":[],"relativePath":"features/drawing-tools.md","filePath":"features/drawing-tools.md"}'),t={name:"features/drawing-tools.md"};function r(s,e,n,h,d,c){return l(),a("div",null,[...e[0]||(e[0]=[o(`<h1 id="drawing-tools" tabindex="-1">Drawing Tools <a class="header-anchor" href="#drawing-tools" aria-label="Permalink to &quot;Drawing Tools&quot;">​</a></h1><p>Complete guide to Whiteboard&#39;s professional drawing tools.</p><h2 id="pen-tool" tabindex="-1">Pen Tool <a class="header-anchor" href="#pen-tool" aria-label="Permalink to &quot;Pen Tool&quot;">​</a></h2><p>The fundamental drawing tool for precise, solid strokes.</p><h3 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h3><p><strong>Activation:</strong> Click pen icon or press <code>P</code></p><p><strong>Properties:</strong></p><ul><li>Solid, consistent color</li><li>Variable size (1-50px)</li><li>Pressure sensitivity (tablet support)</li><li>Clean, precise lines</li></ul><h3 id="best-for" tabindex="-1">Best For <a class="header-anchor" href="#best-for" aria-label="Permalink to &quot;Best For&quot;">​</a></h3><ul><li>Technical diagrams</li><li>Geometric shapes</li><li>Writing text by hand</li><li>Precise line work</li><li>Connecting elements</li></ul><h3 id="settings" tabindex="-1">Settings <a class="header-anchor" href="#settings" aria-label="Permalink to &quot;Settings&quot;">​</a></h3><p><strong>Stroke Size:</strong></p><ul><li>Slider: 1-50px</li><li>Default: 2px</li><li>Per-tool memory: Pen remembers its last size</li></ul><p><strong>Color:</strong></p><ul><li>Any color from picker</li><li>Solid, opaque strokes</li><li>No transparency adjustment</li></ul><p><strong>Pressure Sensitivity:</strong></p><ul><li>Automatic with compatible tablets</li><li>Variable width based on pressure</li><li>No configuration needed</li></ul><h3 id="techniques" tabindex="-1">Techniques <a class="header-anchor" href="#techniques" aria-label="Permalink to &quot;Techniques&quot;">​</a></h3><p><strong>Straight Lines:</strong></p><ol><li>Click starting point</li><li>Hold <code>Shift</code></li><li>Click ending point</li><li>Perfect straight line created</li></ol><p><strong>Quick Sketching:</strong></p><ul><li>Draw at consistent speed</li><li>Lift pen between strokes</li><li>Use undo liberally</li></ul><p><strong>Detail Work:</strong></p><ul><li>Zoom in (Cmd/Ctrl + Scroll)</li><li>Use smaller stroke size (1-3px)</li><li>Draw slowly for control</li></ul><h2 id="smooth-pen-tool" tabindex="-1">Smooth Pen Tool <a class="header-anchor" href="#smooth-pen-tool" aria-label="Permalink to &quot;Smooth Pen Tool&quot;">​</a></h2><p>Advanced pen with calligraphy-style variable-width strokes.</p><h3 id="overview-1" tabindex="-1">Overview <a class="header-anchor" href="#overview-1" aria-label="Permalink to &quot;Overview&quot;">​</a></h3><p><strong>Activation:</strong> Click smooth pen icon or press <code>S</code></p><p><strong>Properties:</strong></p><ul><li>Variable width based on speed</li><li>Natural handwriting feel</li><li>Pressure-sensitive tapering</li><li>Uses perfect-freehand algorithm</li></ul><p><strong>How it works:</strong></p><ul><li>Fast strokes → Thinner width</li><li>Slow strokes → Thicker width</li><li>Natural pen simulation</li></ul><h3 id="best-for-1" tabindex="-1">Best For <a class="header-anchor" href="#best-for-1" aria-label="Permalink to &quot;Best For&quot;">​</a></h3><ul><li>Handwritten text</li><li>Natural annotations</li><li>Artistic drawings</li><li>Expressive content</li><li>Smooth, flowing lines</li></ul><h3 id="settings-1" tabindex="-1">Settings <a class="header-anchor" href="#settings-1" aria-label="Permalink to &quot;Settings&quot;">​</a></h3><p><strong>Base Size:</strong></p><ul><li>Slider: 1-50px</li><li>Default: 4px</li><li>Multiplier for width variation</li></ul><p><strong>Simulation Quality:</strong></p><ul><li>High quality by default</li><li>60fps smooth rendering</li><li>Optimized performance</li></ul><h3 id="techniques-1" tabindex="-1">Techniques <a class="header-anchor" href="#techniques-1" aria-label="Permalink to &quot;Techniques&quot;">​</a></h3><p><strong>Natural Writing:</strong></p><ol><li>Write at normal speed</li><li>Varying width appears automatically</li><li>Lift pen between letters</li><li>Result: Beautiful handwriting</li></ol><p><strong>Emphasis:</strong></p><ul><li>Draw slow for thick emphasis</li><li>Quick strokes for subtle lines</li><li>Vary speed for dynamic effect</li></ul><p><strong>Artistic Strokes:</strong></p><ul><li>Experiment with speed</li><li>Create organic shapes</li><li>Natural brush feel</li></ul><h2 id="highlighter-tool" tabindex="-1">Highlighter Tool <a class="header-anchor" href="#highlighter-tool" aria-label="Permalink to &quot;Highlighter Tool&quot;">​</a></h2><p>Semi-transparent tool for emphasizing existing content.</p><h3 id="overview-2" tabindex="-1">Overview <a class="header-anchor" href="#overview-2" aria-label="Permalink to &quot;Overview&quot;">​</a></h3><p><strong>Activation:</strong> Click highlighter icon or press <code>L</code></p><p><strong>Properties:</strong></p><ul><li>40% opacity (semi-transparent)</li><li>Wide strokes (default 20px)</li><li>Overlays without obscuring</li><li>Bright colors recommended</li></ul><h3 id="best-for-2" tabindex="-1">Best For <a class="header-anchor" href="#best-for-2" aria-label="Permalink to &quot;Best For&quot;">​</a></h3><ul><li>Highlighting important text</li><li>Marking diagram sections</li><li>Color coding content</li><li>Creating emphasis</li><li>Temporary annotations (or use laser)</li></ul><h3 id="settings-2" tabindex="-1">Settings <a class="header-anchor" href="#settings-2" aria-label="Permalink to &quot;Settings&quot;">​</a></h3><p><strong>Opacity:</strong></p><ul><li>Fixed at 40%</li><li>Cannot be adjusted (by design)</li><li>Ensures readability</li></ul><p><strong>Size:</strong></p><ul><li>Default: 20px (wide)</li><li>Adjustable 5-50px</li><li>Wider = more visible</li></ul><p><strong>Colors:</strong></p><ul><li>Bright colors work best (yellow, orange, pink)</li><li>Avoid dark colors (less visible)</li><li>Multiple colors for coding</li></ul><h3 id="techniques-2" tabindex="-1">Techniques <a class="header-anchor" href="#techniques-2" aria-label="Permalink to &quot;Techniques&quot;">​</a></h3><p><strong>Underlining:</strong></p><ul><li>Draw under text to emphasize</li><li>Single pass for subtle</li><li>Multiple passes for darker</li></ul><p><strong>Blocking:</strong></p><ul><li>Fill area with overlapping strokes</li><li>Highlight entire section</li><li>Color-code different topics</li></ul><p><strong>Layering:</strong></p><ul><li>Overlap colors for mix</li><li>Orange + yellow = darker orange</li><li>Build up opacity gradually</li></ul><h2 id="eraser-tool" tabindex="-1">Eraser Tool <a class="header-anchor" href="#eraser-tool" aria-label="Permalink to &quot;Eraser Tool&quot;">​</a></h2><p>Remove individual strokes from the canvas.</p><h3 id="overview-3" tabindex="-1">Overview <a class="header-anchor" href="#overview-3" aria-label="Permalink to &quot;Overview&quot;">​</a></h3><p><strong>Activation:</strong> Click eraser icon or press <code>E</code></p><p><strong>Behavior:</strong></p><ul><li>Click stroke → removes entire stroke</li><li>Hover shows which stroke will erase</li><li>No partial erasing (complete strokes only)</li></ul><h3 id="best-for-3" tabindex="-1">Best For <a class="header-anchor" href="#best-for-3" aria-label="Permalink to &quot;Best For&quot;">​</a></h3><ul><li>Removing mistakes</li><li>Cleaning up overlaps</li><li>Deleting unwanted strokes</li><li>Quick corrections</li></ul><h3 id="limitations" tabindex="-1">Limitations <a class="header-anchor" href="#limitations" aria-label="Permalink to &quot;Limitations&quot;">​</a></h3><p><strong>Cannot erase:</strong></p><ul><li>Smart objects (text, code, LaTeX)</li><li>Images</li><li>Shapes (use delete instead)</li></ul><p><strong>Erases:</strong></p><ul><li>Pen strokes</li><li>Smooth pen strokes</li><li>Highlighter strokes</li><li>Laser strokes (but they fade anyway)</li></ul><h3 id="techniques-3" tabindex="-1">Techniques <a class="header-anchor" href="#techniques-3" aria-label="Permalink to &quot;Techniques&quot;">​</a></h3><p><strong>Selective Removal:</strong></p><ol><li>Hover over stroke</li><li>Preview highlight shows target</li><li>Click to remove</li><li>Undo if wrong stroke</li></ol><p><strong>Batch Erasing:</strong></p><ul><li>Click multiple strokes quickly</li><li>Each click removes one stroke</li><li>Use undo for oops moments</li></ul><p><strong>Alternative: Undo:</strong></p><ul><li><code>Cmd/Ctrl + Z</code> often better</li><li>Removes last action</li><li>Can undo multiple times</li><li>Preserves other work</li></ul><h2 id="shape-tools" tabindex="-1">Shape Tools <a class="header-anchor" href="#shape-tools" aria-label="Permalink to &quot;Shape Tools&quot;">​</a></h2><p>Create perfect geometric shapes.</p><h3 id="rectangle-tool" tabindex="-1">Rectangle Tool <a class="header-anchor" href="#rectangle-tool" aria-label="Permalink to &quot;Rectangle Tool&quot;">​</a></h3><p><strong>Usage:</strong></p><ol><li>Select rectangle tool</li><li>Click and drag</li><li>Release for completed rectangle</li><li>Perfect right angles and parallel sides</li></ol><p><strong>Modifiers:</strong></p><ul><li><code>Shift + Drag</code>: Constrain to square</li><li>Regular drag: Any rectangle</li></ul><p><strong>Properties:</strong></p><ul><li>Border color matches current color</li><li>Border width matches current size</li><li>No fill (transparent inside)</li></ul><h3 id="circle-tool" tabindex="-1">Circle Tool <a class="header-anchor" href="#circle-tool" aria-label="Permalink to &quot;Circle Tool&quot;">​</a></h3><p><strong>Usage:</strong></p><ol><li>Select circle tool</li><li>Click and drag</li><li>Release for completed circle</li><li>Perfect circular shape</li></ol><p><strong>Modifiers:</strong></p><ul><li><code>Shift + Drag</code>: Constrain to perfect circle</li><li>Regular drag: Can be oval</li></ul><p><strong>Properties:</strong></p><ul><li>Border color matches current color</li><li>Border width matches current size</li><li>No fill (transparent inside)</li></ul><h3 id="arrow-tool" tabindex="-1">Arrow Tool <a class="header-anchor" href="#arrow-tool" aria-label="Permalink to &quot;Arrow Tool&quot;">​</a></h3><p><strong>Usage:</strong></p><ol><li>Select arrow tool</li><li>Click and drag for direction</li><li>Release when positioned</li><li>Arrow head automatically added</li></ol><p><strong>Properties:</strong></p><ul><li>Straight line with arrow head</li><li>Directional indicator</li><li>Configurable size and color</li></ul><h3 id="best-for-4" tabindex="-1">Best For <a class="header-anchor" href="#best-for-4" aria-label="Permalink to &quot;Best For&quot;">​</a></h3><p><strong>Rectangles:</strong></p><ul><li>Boxes and containers</li><li>Diagrams and flowcharts</li><li>Highlighting regions</li><li>Creating structure</li></ul><p><strong>Circles:</strong></p><ul><li>Circling important items</li><li>Venn diagrams</li><li>State diagrams</li><li>Bubbles and nodes</li></ul><p><strong>Arrows:</strong></p><ul><li>Showing flow/direction</li><li>Connecting elements</li><li>Indicating movement</li><li>Process flows</li></ul><h2 id="hand-tool-pan" tabindex="-1">Hand Tool (Pan) <a class="header-anchor" href="#hand-tool-pan" aria-label="Permalink to &quot;Hand Tool (Pan)&quot;">​</a></h2><p>Navigate the canvas without drawing.</p><h3 id="overview-4" tabindex="-1">Overview <a class="header-anchor" href="#overview-4" aria-label="Permalink to &quot;Overview&quot;">​</a></h3><p><strong>Activation:</strong></p><ul><li>Click hand icon or press <code>H</code></li><li>Or hold <code>Space</code> bar (temporary)</li></ul><p><strong>Usage:</strong></p><ul><li>Click and drag to move canvas</li><li>Doesn&#39;t create any marks</li><li>Navigation only</li></ul><h3 id="techniques-4" tabindex="-1">Techniques <a class="header-anchor" href="#techniques-4" aria-label="Permalink to &quot;Techniques&quot;">​</a></h3><p><strong>Quick Pan:</strong></p><ol><li>Hold <code>Space</code> while using any tool</li><li>Drag to reposition</li><li>Release <code>Space</code> to resume drawing</li><li>No tool switching needed</li></ol><p><strong>Scroll Alternative:</strong></p><ul><li>Two-finger drag on trackpad</li><li>Mouse wheel scroll (without Cmd/Ctrl)</li></ul><h2 id="tool-comparison" tabindex="-1">Tool Comparison <a class="header-anchor" href="#tool-comparison" aria-label="Permalink to &quot;Tool Comparison&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Tool</th><th>Opacity</th><th>Variable Width</th><th>Use Case</th></tr></thead><tbody><tr><td>Pen</td><td>100%</td><td>No</td><td>Precise lines</td></tr><tr><td>Smooth Pen</td><td>100%</td><td>Yes (speed)</td><td>Natural writing</td></tr><tr><td>Highlighter</td><td>40%</td><td>No</td><td>Emphasis</td></tr><tr><td>Eraser</td><td>N/A</td><td>N/A</td><td>Removal</td></tr><tr><td>Shapes</td><td>100%</td><td>No</td><td>Perfect geometry</td></tr></tbody></table><h2 id="advanced-techniques" tabindex="-1">Advanced Techniques <a class="header-anchor" href="#advanced-techniques" aria-label="Permalink to &quot;Advanced Techniques&quot;">​</a></h2><h3 id="layering-tools" tabindex="-1">Layering Tools <a class="header-anchor" href="#layering-tools" aria-label="Permalink to &quot;Layering Tools&quot;">​</a></h3><p>Combine tools for rich content:</p><p><strong>Example: Annotated diagram</strong></p><ol><li>Shapes: Draw boxes and circles</li><li>Pen: Add connecting lines</li><li>Smooth pen: Write labels</li><li>Highlighter: Emphasize key parts</li></ol><h3 id="speed-workflow" tabindex="-1">Speed Workflow <a class="header-anchor" href="#speed-workflow" aria-label="Permalink to &quot;Speed Workflow&quot;">​</a></h3><p>Minimize tool switching:</p><p><strong>Left hand:</strong> <code>P</code>, <code>S</code>, <code>E</code>, <code>H</code> (tool shortcuts) <strong>Right hand:</strong> Draw with mouse/tablet <strong>Result:</strong> Never touch toolbar, 5x faster</p><h3 id="tablet-optimization" tabindex="-1">Tablet Optimization <a class="header-anchor" href="#tablet-optimization" aria-label="Permalink to &quot;Tablet Optimization&quot;">​</a></h3><p>Using a drawing tablet?</p><p><strong>Settings:</strong></p><ul><li>Enable pressure sensitivity</li><li>Use smooth pen for natural feel</li><li>Configure pen buttons for undo/tool switch</li><li>Adjust tablet mapping to screen area</li></ul><h3 id="precision-techniques" tabindex="-1">Precision Techniques <a class="header-anchor" href="#precision-techniques" aria-label="Permalink to &quot;Precision Techniques&quot;">​</a></h3><p><strong>For perfect results:</strong></p><ol><li>Zoom in (200%+)</li><li>Use smaller stroke size</li><li>Draw slowly and deliberately</li><li>Use shapes for geometry</li><li>Undo and redraw if needed</li></ol><h2 id="common-patterns" tabindex="-1">Common Patterns <a class="header-anchor" href="#common-patterns" aria-label="Permalink to &quot;Common Patterns&quot;">​</a></h2><h3 id="diagram-creation" tabindex="-1">Diagram Creation <a class="header-anchor" href="#diagram-creation" aria-label="Permalink to &quot;Diagram Creation&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>1. Shapes: Create boxes/circles (main elements)</span></span>
<span class="line"><span>2. Pen: Connect with arrows/lines</span></span>
<span class="line"><span>3. Smooth pen: Add labels</span></span>
<span class="line"><span>4. Highlighter: Emphasize relationships</span></span></code></pre></div><h3 id="handwritten-notes" tabindex="-1">Handwritten Notes <a class="header-anchor" href="#handwritten-notes" aria-label="Permalink to &quot;Handwritten Notes&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>1. Smooth pen: Natural handwriting</span></span>
<span class="line"><span>2. Highlighter: Mark important points</span></span>
<span class="line"><span>3. Pen: Underline titles (straight lines)</span></span>
<span class="line"><span>4. Eraser: Fix mistakes</span></span></code></pre></div><h3 id="technical-drawing" tabindex="-1">Technical Drawing <a class="header-anchor" href="#technical-drawing" aria-label="Permalink to &quot;Technical Drawing&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>1. Pen: Precise lines</span></span>
<span class="line"><span>2. Shapes: Perfect geometry</span></span>
<span class="line"><span>3. Pen + Shift: Straight connections</span></span>
<span class="line"><span>4. Small size: Fine details</span></span></code></pre></div><h2 id="tips-tricks" tabindex="-1">Tips &amp; Tricks <a class="header-anchor" href="#tips-tricks" aria-label="Permalink to &quot;Tips &amp; Tricks&quot;">​</a></h2><h3 id="drawing-smooth-curves" tabindex="-1">Drawing Smooth Curves <a class="header-anchor" href="#drawing-smooth-curves" aria-label="Permalink to &quot;Drawing Smooth Curves&quot;">​</a></h3><p><strong>With pen:</strong></p><ul><li>Draw quickly in one motion</li><li>Don&#39;t pause mid-stroke</li><li>Undo and retry if jagged</li></ul><p><strong>With smooth pen:</strong></p><ul><li>Natural speed works best</li><li>Algorithm smooths automatically</li><li>More forgiving than pen</li></ul><h3 id="perfect-circles-by-hand" tabindex="-1">Perfect Circles by Hand <a class="header-anchor" href="#perfect-circles-by-hand" aria-label="Permalink to &quot;Perfect Circles by Hand&quot;">​</a></h3><ol><li>Draw rough circle with pen</li><li>Switch to magic wand (<code>M</code>)</li><li>Click on rough circle</li><li>Transforms to perfect circle!</li></ol><p><a href="/whiteboard/docs/features/animations.html">Learn more about Magic Animations →</a></p><h3 id="color-coding" tabindex="-1">Color Coding <a class="header-anchor" href="#color-coding" aria-label="Permalink to &quot;Color Coding&quot;">​</a></h3><p>Use consistent colors for different types:</p><p><strong>Example system:</strong></p><ul><li>Blue: Main concepts</li><li>Green: Examples</li><li>Orange: Key terms</li><li>Red: Important warnings</li><li>Yellow highlighter: Emphasis</li></ul><h3 id="size-management" tabindex="-1">Size Management <a class="header-anchor" href="#size-management" aria-label="Permalink to &quot;Size Management&quot;">​</a></h3><p><strong>General guidelines:</strong></p><ul><li>Titles: 10-20px</li><li>Body: 3-5px</li><li>Details: 1-2px</li><li>Highlighter: 15-25px</li><li>Shapes: 2-4px borders</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="strokes-look-jagged" tabindex="-1">Strokes look jagged <a class="header-anchor" href="#strokes-look-jagged" aria-label="Permalink to &quot;Strokes look jagged&quot;">​</a></h3><p><strong>Solutions:</strong></p><ul><li>Use smooth pen instead</li><li>Draw more slowly</li><li>Zoom in while drawing</li><li>Use tablet instead of mouse</li></ul><h3 id="can-t-draw-on-canvas" tabindex="-1">Can&#39;t draw on canvas <a class="header-anchor" href="#can-t-draw-on-canvas" aria-label="Permalink to &quot;Can&#39;t draw on canvas&quot;">​</a></h3><p><strong>Check:</strong></p><ul><li>Not using hand/eraser tool</li><li>Canvas is focused (click first)</li><li>Not in presentation window (read-only)</li></ul><h3 id="colors-not-changing" tabindex="-1">Colors not changing <a class="header-anchor" href="#colors-not-changing" aria-label="Permalink to &quot;Colors not changing&quot;">​</a></h3><p><strong>Solutions:</strong></p><ul><li>Actually select different color</li><li>Check color picker is open</li><li>Verify tool supports color</li><li>Refresh if stuck</li></ul><h3 id="tool-won-t-switch" tabindex="-1">Tool won&#39;t switch <a class="header-anchor" href="#tool-won-t-switch" aria-label="Permalink to &quot;Tool won&#39;t switch&quot;">​</a></h3><p><strong>Solutions:</strong></p><ul><li>Click canvas to focus</li><li>Try keyboard shortcut</li><li>Refresh page</li><li>Check browser console for errors</li></ul><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ul><li><a href="/whiteboard/docs/guide/smart-objects.html">Learn Smart Objects →</a></li><li><a href="/whiteboard/docs/guide/shortcuts.html">Master Keyboard Shortcuts →</a></li><li><a href="/whiteboard/docs/features/animations.html">Explore Animations →</a></li></ul><h2 id="tool-philosophy" tabindex="-1">Tool Philosophy <a class="header-anchor" href="#tool-philosophy" aria-label="Permalink to &quot;Tool Philosophy&quot;">​</a></h2><p>Whiteboard&#39;s tools are designed around these principles:</p><ol><li><strong>Simple by default:</strong> Basic tools are straightforward</li><li><strong>Powerful when needed:</strong> Advanced features available</li><li><strong>Familiar:</strong> Similar to other drawing apps</li><li><strong>Optimized for teaching:</strong> Focus on clarity over artistry</li><li><strong>Fast workflow:</strong> Keyboard shortcuts for efficiency</li></ol><p>Start simple, master the basics, then explore advanced techniques!</p>`,187)])])}const u=i(t,[["render",r]]);export{g as __pageData,u as default};
